import { useEffect, useState } from "react";
import styled from "styled-components";
import PersonIcon from "@mui/icons-material/Person";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import { useWindowScroll } from "react-use";
import { RiMenu5Line } from 'react-icons/ri';
import { Link } from "react-router-dom";
import { useSelector } from "react-redux";



const Header = styled.div`
  display: flex;
  height: 30px;
  position: relative ;
  background: rgba(0,0,0,0.5);
  align-items: center;
  justify-content: space-between;
  top: 0;
  z-index: 1000;
  padding: 20px 20px;
  transition: background-color 0.3s ease;
  box-shadow: 0px 0px 17px -2px rgba(0, 0, 0, 0.75);

  @media (max-width: 768px) {
    padding: 20px;
  }
`;


const Logo = styled.img`
  height: 70px;
  border-radius: 50%;
  cursor: pointer;


  @media (max-width: 768px) {
    margin-right: 20px;
  }
`;

const Nav = styled.ul`
  display: flex;
  margin-left: 90px;

  @media (max-width: 768px) {
    display: none;
  }
`;

const NavItem = styled.li`
  margin: 0 10px;
  padding: 0px 50px;
  font-size: 18px;
  list-style: none;
  color: #000;
  font-style: oblique;
  font-size: 20px;
  font-weight: bold;
  border-bottom: 0.1rem solid transparent;
  cursor: pointer;

  &:hover {
    color: #f00;
  }

  @media (max-width: 768px) {
    margin-left: 20px;
  }
`;

const CartIcon = styled.div`
  display: flex;
  color: #000;
  margin-right: 20px;
  cursor: pointer;

  &:hover {
    color: #f00;
  }

  @media (max-width: 768px) {
    margin-right: 40px;
  }
`;
const PerIcon = styled.div`
  cursor: pointer;
  @media (max-width: 768px) {
    display:none;
  }
`;

const BarIcon = styled.div`
  display: flex;
  color: #000;
  margin-right: 20px;
  display: none;

  &:hover {
    color: #f00;
  }

  @media (max-width: 768px) {
    display: block;
    margin-right: 40px;
  }
`;

const Mobil = styled.div`
  display: flex;
  color: #fff;
  flex-direction: column;
  justify-content: space-between;
  position: absolute;
  top: 70px;
  right: 10px;
  background-color: #333;
  padding: 10px;
  border-radius: 10px;
  height: 300px;

  
  
  img {
    margin-top: 25px;
    height: 95px;
    width: 195px;
    border-radius: 10px;
    object-fit: cover;
    margin-bottom: 30px;

  }

  span {
  z-index: 999;
  list-style: none;
  color: #fff;
  font-style: oblique;
  font-size: 20px;
  font-weight: bold;
  border-bottom: 0.1rem solid transparent;
  text-decoration: none;
  }


`;

const Navbar = () => {
  const [showMenu, setShowMenu] = useState(false);
  const { y: scrollY } = useWindowScroll();
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    if (scrollY > 0) {
      setScrolled(true);
    } else {
      setScrolled(false);
    }
  }, [scrollY]);


  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const handleMenu = () => {
    setIsMenuOpen(!isMenuOpen); // Menu durumunu tersine çevir (açıksa kapat, kapalıysa aç)
  };



  const cartItems = useSelector((state) => state.cart.items);
  const totalItems = cartItems.reduce((total, item) => total + item.amount, 0);


  return (

    <Header scrolled={scrolled}>
    <Link to="/">
      <Logo
        style={{ marginRight: 20 }}
        src="data:image/png;base64,"
        alt="Logo"
      />
      </Link>
      <Nav>
        <Link to="/"><NavItem>Ana Sayfa</NavItem></Link>
        <Link to="/wiki"><NavItem>H.P - WIKI</NavItem></Link>
        <NavItem>Ürünler</NavItem>
        <Link to="/iletisim"><NavItem>İletişim</NavItem></Link>
      </Nav>
      <PerIcon onClick={handleMenu}>
        <PersonIcon onClick={handleMenu} style={{ fontSize: 30 }} />
      </PerIcon>
      {isMenuOpen && (
        <Mobil>
          <img
            src="https://media.glamourmagazine.co.uk/photos/637ba02321878c020d3a6901/3:2/w_1920,h_1280,c_limit/HARRYPOTTERFILMS%20211122%20default-sq-Daniel-Radcliffe-5.jpg"
            alt="Mobil Image"
          />
        </Mobil>
      )}
      <CartIcon>
        <Link to="/sepet"><ShoppingCartIcon style={{ fontSize: 30 }} /></Link>
        <span>{totalItems}</span>
      </CartIcon>
      <BarIcon onClick={() => setShowMenu(!showMenu)}>
        <RiMenu5Line style={{ fontSize: 30 }} />
      </BarIcon>
      {showMenu && (
        <Mobil>
          <img
            src="https://media.glamourmagazine.co.uk/photos/637ba02321878c020d3a6901/3:2/w_1920,h_1280,c_limit/HARRYPOTTERFILMS%20211122%20default-sq-Daniel-Radcliffe-5.jpg
            "
            alt="Mobil Image"
          />
          <Link to="/"><span>Ana Sayfa</span></Link>
          <Link to="/wiki"><span>WIKI</span></Link>
          <span>Ürünler</span>
          <Link to="/iletisim"><span>İletişim</span></Link>
        </Mobil>
      )}
    </Header>
  );
};

export default Navbar;


